jQuery( document ).ready( function ( $ ) {
	jQuery( '.btn-list .apply' ).click( function ( e ) {
		if ( location.pathname.replace( /^\//, '' ) == this.pathname.replace( /^\//, '' ) && location.hostname == this.hostname ) {
			let target = jQuery( this.hash );
			target = target.length ? target : jQuery( '[name=' + this.hash.slice( 1 ) + ']' );
			if ( target.length ) {
				jQuery( 'html, body' ).animate( {
					scrollTop: (target.offset().top - 120)
				}, 1000 );
				return false;
			}
		}
	} );

	if ( jQuery( '#footer_custom_text' ).length > 0 ) {
		let get_footer_text = jQuery( '#footer_custom_text' ).text();
		jQuery( '.footer-cta h2' ).text( get_footer_text );
	}

	jQuery( '.menu-item-has-children' ).find( '.sub-menu' ).before( '<span class="footer-submenu-toggle"><img src="/wp-content/themes/pyramid/static/images/svgs/menu-drop-white.svg"></span>' );
	jQuery( '.footer-submenu-toggle' ).on( 'click', (function ( e ) {
		e.preventDefault();
		let submenu = $( this ).next( 'ul.sub-menu' );
		submenu.slideToggle();
	}) );

	jQuery( '.sf-field-submit' ).html( '<button type="submit" name="_sf_submit"><img src="/wp-content/uploads/2022/02/search.png"/></button>' );
	jQuery( '.sf-field-category ul li:nth-child(1)' ).remove();
	jQuery( '.sf-field-category ul li:nth-child(1)' ).addClass( 'sf-option-active' );

	var windowsize = $(window).width();

	$(window).resize(function() {
	  var windowsize = $(window).width();
	});
	if (windowsize < 993) 
	{
		var mobileimage = $('.mobile-footer-image').val();
		$('.desktop-bg-image').css('background-image', 'url(' + mobileimage + ')');
		$('.desktop-bg-image').addClass('mobile-bg-image');
	}
	else
	{
		var desktopimage = $('.desktop-footer-image').val();
		$('.desktop-bg-image').css('background-image', 'url(' + desktopimage + ')');
		$('.desktop-bg-image').removeClass('mobile-bg-image');
	}

} );